import React, { useState, useEffect } from 'react';

function IndividualStudent() {
  const [concertData, setConcertData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(3);
  const [concertData2, setConcertData2] = useState([]);
  const [searchedId, setSearchedId] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8081/ShowAndId');
        const data = await response.json();
        if (Array.isArray(data)) {
          setConcertData(data);
        } else {
          setConcertData([]);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setConcertData([]);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    const fetchDataDown = async () => {
      try {
        const response = await fetch('http://localhost:8081/ShowAndIdDown');
        const data = await response.json();
        if (Array.isArray(data)) {
          setConcertData2(data);
        } else {
          setConcertData2([]);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setConcertData2([]);
      }
    };

    fetchDataDown();
  }, []);

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = concertData.slice(indexOfFirstItem, indexOfLastItem);
  const currentItems2 = concertData2.slice(indexOfFirstItem, indexOfLastItem);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const singleSearch = () => {
    const filteredData = concertData.filter((student) => student.id === parseInt(searchedId));
    setConcertData2(filteredData);
  };

  return (
    <>
      <div className='text-center top'>
        <h5 className='colorw'>All Students List</h5>
        <div className='text-center table-box center2'>
          <div>
            <h6 className='colorw'>Search Student </h6>
            <input
              type='number'
              className='border'
              onChange={(e) => setSearchedId(e.target.value)}
              placeholder='Enter Student ID'
            />
            <button type='button' className='button border' onClick={singleSearch}>
              Search
            </button>
          </div>
        </div>
      </div>

      <div className='text-center center2'>
        <table className='table2'>
          <thead>
            <tr>
              <th>Student ID</th>
              <th>Student Name</th>
              <th>Student Email</th>
              <th>Student Username</th>
            </tr>
          </thead>
          <tbody className='tbody'>
            {currentItems2.map((student, index) => (
              <tr key={index}>
                <td>{student.id}</td>
                <td>{student.name}</td>
                <td>{student.email}</td>
                <td>{student.username}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
}

export default IndividualStudent;
