import React, { useState,useEffect } from 'react';
import axios from 'axios';import PropTypes from 'prop-types';

export default function Text_area(props) {
  var val;
    // const [tno, setTno] = useState('');
    const [tname, setTname] = useState('');
    const [status, setStatus] = useState(false);
    const [concertData, setConcertData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8081/ShowAndId');
        const data = await response.json();
        if (Array.isArray(data)) {
          setConcertData(data);
          console.log(concertData.map(item => item.tname).join('\n'))
        } else {
          setConcertData([]);
        }
      } catch (error) {
        console.error('Error:', error);
        setConcertData([]);

      }
    };
    fetchData();
  }, []);

    const [values, setValues] = useState({
        tname: "",
        
      });

      const handleInput = (event) => {
        setValues((prev) => ({ ...prev, [event.target.name]: event.target.value }));
      };
    
     
      const handleSubmit = (e) => {
        e.preventDefault();
        // const err=validation(values);
        // setErrors(err)
        // if(err.name==="" && err.email==="" && err.password===""){
        fetch("http://localhost:8081/Create", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            tname: values.tname,
           
          }),
        })
          .then((res) => res.json())
          .then((data) => {
            if (data.message) {
            //   navigate("/Created");
            val=" ";
            } else {
              console.log(data);
            //   navigate("/Created");
            }
          })
          .catch((error) => {
            console.error("Error:", error);
          });
        // }
      };
    

  return (
    <div>
        <h5 className=' colorw center2'> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{props.heading}</h5>

<textarea rows="2" value={concertData} cols="50" disabled>

</textarea>
<div className='d-inline-flex'  >
<input name='tname' type="text" value={concertData.map(item => item.tname).join('\n')}onChange={handleInput}  placeholder="Enter Text to Add" />
<input name='tno' type='number' placeholder='Id for below function'></input>
</div>



<div className="d-inline-flex">
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
{props.text1!==""?<button onClick={handleSubmit} className='btn btn-success'> {props.text1}</button>
:<></>}
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
{props.text2!==""?<button className='btn btn-warning' > {props.text2}</button>
:<></>}

{props.text3!==""?<button className='btn btn-danger'> {props.text3}</button>
:<></>}

{props.text4!==undefined?<button  className='btn btn-primary'> {props.text4}</button>
:<></>}
</div>

    </div>
  )
}
