import React, { useState } from 'react';
import axios from 'axios';import PropTypes from 'prop-types';

export default function Text_area(props) {
    // const [tno, setTno] = useState('');
    const [tname, setTname] = useState('');
    const [status, setStatus] = useState(false);
  
    const handleAddTodo = () => {
      axios.post('http://localhost:8081/AddTodo', { tname })
        .then(response => {
          console.log(response.data);
          // Handle success, maybe show a success message or update state
          setStatus(true);
          co
        })
        .catch(error => {
          console.error('Error adding todo:', error);
          // Handle error, maybe show an error message
        });
    };

  return (
    <div>
        <h5 className=' colorw center2'> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{props.heading}</h5>

<textarea rows="2" value={props.results} cols="50" disabled>

</textarea>
<div className='d-inline-flex'  >
<input type="text" value={tname} onChange={e => setTname(e.target.value)} placeholder="Enter Text to Add" />
<input type='number' placeholder='Id for below function'></input>
</div>



<div className="d-inline-flex">
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
{props.text1!==""?<button className='btn btn-success'> {props.text1}</button>
:<></>}
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
{props.text2!==""?<button className='btn btn-warning' > {props.text2}</button>
:<></>}

{props.text3!==""?<button className='btn btn-danger'> {props.text3}</button>
:<></>}

{props.text4!==undefined?<button onClick={handleAddTodo} className='btn btn-primary'> {props.text4}</button>
:<></>}
</div>

    </div>
  )
}
